import { Component } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { of } from 'rxjs';

@Component({
  selector: 'app-repair-request-form',
  templateUrl: './repair-request-form.component.html',
  styleUrls: ['./repair-request-form.component.css'],
})
export class RepairRequestFormComponent {
  name: string | any;
  email: string | any;
  description: string | any;

  constructor(private http: HttpClient) {}

  submitForm() {
    const repairRequest = {
      name: this.name,
      email: this.email,
      description: this.description,
    };

    this.http
      .post('http://localhost:5000/api/repair-requests', repairRequest)
      .subscribe(
        () => {
          console.log('Repair request submitted successfully.');
        },
        (error) => {
          console.error(
            'An error occurred while submitting the repair request:',
            error
          );
        }
      );
  }
}
<h2>Submit Repair Request</h2>
<form (ngSubmit)="submitForm()">
  <div class="form-item">
    <label for="name">Name:</label>
    <input type="text" id="name" [(ngModel)]="name" name="name" required />
  </div>
  <div class="form-item">
    <label for="email">Email:</label>
    <input type="email" id="email" [(ngModel)]="email" name="email" required />
  </div>
  <div class="form-item">
    <label for="description">Description:</label>
    <textarea
      id="description"
      [(ngModel)]="description"
      name="description"
      required
    ></textarea>
  </div>
  <button type="submit">Submit</button>
</form>
